    stateName: TStateName,
    stateUpdaterName: TStateUpdaterName,
    initialState: TState | Mapper<TOuter, TState>
): InferableComponentEnhancerWithProps<
    StateProps<TState, TStateName, TStateUpdaterName>,
    TOuter
> => (BaseComponent: any): any => {
